-- ----------------------------
-- Table structure for test
-- ----------------------------
DROP TABLE IF EXISTS `test`;
CREATE TABLE `test`  (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `title` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL,
  `content` text CHARACTER SET utf8 COLLATE utf8_general_ci NULL,
  `type` int(4) UNSIGNED NULL DEFAULT 1 COMMENT '0本文，1自己要求',
  PRIMARY KEY (`id`) USING BTREE,
  UNIQUE INDEX `title`(`title`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 40 CHARACTER SET = utf8 COLLATE = utf8_general_ci ROW_FORMAT = Dynamic;

-- ----------------------------
-- Records of test
-- ----------------------------
INSERT INTO `test` VALUES (1, '约翰一书 4:1-6 和合本', '亲爱的弟兄啊，一切的灵，你们不可都信，总要试验那些灵是出于　神的不是，因为世上有许多假先知已经出来了。凡灵认耶稣基督是成了肉身来的，就是出于　神的；从此你们可以认出　神的灵来。凡灵不认耶稣，就不是出于　神，这是那敌基督者的灵。你们从前听见他要来，现在已经在世上了。小子们哪，你们是属　神的，并且胜了他们；因为那在你们里面的，比那在世界上的更大。他们是属世界的，所以论世界的事，世人也听从他们。我们是属　神的，认识　神的就听从我们；不属　神的就不听从我们。从此我们可以认出真理的灵和谬妄的灵来。', 0);
INSERT INTO `test` VALUES (2, '约翰一书 5:13 和合本', '我将这些话写给你们信奉　神儿子之名的人，要叫你们知道自己有永生。', 0);
INSERT INTO `test` VALUES (3, '诗篇 78:2 和合本', '我要开口说比喻；\r\n我要说出古时的谜语，', 0);
INSERT INTO `test` VALUES (4, '马太福音 13:34-35 和合本', '这都是耶稣用比喻对众人说的话；若不用比喻，就不对他们说什么。这是要应验先知的话，说：\r\n我要开口用比喻，\r\n把创世以来所隐藏的事发明出来。', 0);
INSERT INTO `test` VALUES (5, '约翰福音 16:25 和合本', '“这些事，我是用比喻对你们说的；时候将到，我不再用比喻对你们说，乃要将父明明地告诉你们。', 0);
INSERT INTO `test` VALUES (6, '约翰福音 14:29 和合本', '现在事情还没有成就，我预先告诉你们，叫你们到事情成就的时候就可以信。', 0);
INSERT INTO `test` VALUES (7, '马太福音 13:31-32 和合本', '他又设个比喻对他们说：“天国好像一粒芥菜种，有人拿去种在田里。这原是百种里最小的，等到长起来，却比各样的菜都大，且成了树，天上的飞鸟来宿在它的枝上。”', 0);
INSERT INTO `test` VALUES (8, '诗篇 73:22 和合本', '我这样愚昧无知，\r\n在你面前如畜类一般。', 1);
INSERT INTO `test` VALUES (9, '约翰福音 5:39-40 和合本', '你们查考圣经（或译：应当查考圣经），因你们以为内中有永生；给我作见证的就是这经。然而，你们不肯到我这里来得生命。', 1);
INSERT INTO `test` VALUES (10, '马太福音 13:10-11 和合本', '门徒进前来，问耶稣说：“对众人讲话，为什么用比喻呢？”耶稣回答说：“因为天国的奥秘只叫你们知道，不叫他们知道。', 1);
INSERT INTO `test` VALUES (11, '马太福音 19:17 和合本', '耶稣对他说：“你为什么以善事问我呢？只有一位是善的（有古卷：你为什么称我是良善的？除了　神以外，没有一个良善的）。你若要进入永生，就当遵守诫命。”', 1);
INSERT INTO `test` VALUES (12, '约翰福音 15:18-19 和合本', '“世人若恨你们，你们知道（或译：该知道），恨你们以先已经恨我了。你们若属世界，世界必爱属自己的；只因你们不属世界，乃是我从世界中拣选了你们，所以世界就恨你们。', 1);
INSERT INTO `test` VALUES (13, '希伯来书 3:4 和合本', '因为房屋都必有人建造，但建造万物的就是　神。', 1);
INSERT INTO `test` VALUES (14, '约翰福音 4:24 和合本', '神是个灵（或无个字），所以拜他的必须用心灵和诚实拜他。', 1);
INSERT INTO `test` VALUES (15, '约翰福音 8:44 和合本', '你们是出于你们的父魔鬼，你们父的私欲你们偏要行。他从起初是杀人的，不守真理，因他心里没有真理。他说谎是出于自己；因他本来是说谎的，也是说谎之人的父。', 1);
INSERT INTO `test` VALUES (16, '约翰福音 6:44 和合本', '若不是差我来的父吸引人，就没有能到我这里来的；到我这里来的，在末日我要叫他复活。', 1);
INSERT INTO `test` VALUES (17, '提多书 1:2 和合本', '盼望那无谎言的　神在万古之先所应许的永生，', 1);
INSERT INTO `test` VALUES (18, '罗马书 6:23 和合本', '因为罪的工价乃是死；惟有　神的恩赐，在我们的主基督耶稣里，乃是永生。', 1);
INSERT INTO `test` VALUES (19, '约翰福音 1:1-4 和合本', '太初有道，道与　神同在，道就是　神。这道太初与　神同在。万物是藉着他造的；凡被造的，没有一样不是藉着他造的。生命在他里头，这生命就是人的光。', 1);
INSERT INTO `test` VALUES (20, '传道书 3:11 和合本', '神造万物，各按其时成为美好，又将永生（原文是永远）安置在世人心里。然而　神从始至终的作为，人不能参透。', 1);
INSERT INTO `test` VALUES (21, '约翰一书 2:25 和合本', '主所应许我们的就是永生。', 1);
INSERT INTO `test` VALUES (22, '约翰福音 5:39 和合本', '你们查考圣经（或译：应当查考圣经），因你们以为内中有永生；给我作见证的就是这经。', 1);
INSERT INTO `test` VALUES (23, '约翰福音 6:40 和合本', '因为我父的意思是叫一切见子而信的人得永生，并且在末日我要叫他复活。”', 1);
INSERT INTO `test` VALUES (24, '约翰福音 12:50 和合本', '我也知道他的命令就是永生。故此，我所讲的话正是照着父对我所说的。”', 1);
INSERT INTO `test` VALUES (25, '哥林多前书 1:26-29 和合本', '弟兄们哪，可见你们蒙召的，按着肉体有智慧的不多，有能力的不多，有尊贵的也不多。　神却拣选了世上愚拙的，叫有智慧的羞愧；又拣选了世上软弱的，叫那强壮的羞愧。　神也拣选了世上卑贱的，被人厌恶的，以及那无有的，为要废掉那有的，使一切有血气的，在　神面前一个也不能自夸。', 1);
INSERT INTO `test` VALUES (29, '耶利米书 15:16 和合本', '耶和华－万军之　神啊，\r\n我得着你的言语就当食物吃了；\r\n你的言语是我心中的欢喜快乐，\r\n因我是称为你名下的人。', 1);
INSERT INTO `test` VALUES (30, '提摩太后书 3:16 和合本', '圣经都是　神所默示的（或译：凡　神所默示的圣经），于教训、督责、使人归正、教导人学义都是有益的，', 1);
INSERT INTO `test` VALUES (31, '彼得后书 1:21 和合本', '因为预言从来没有出于人意的，乃是人被圣灵感动，说出　神的话来。', 1);
INSERT INTO `test` VALUES (32, '哥林多前书 2:9-13 和合本', '如经上所记：\r\n　神为爱他的人所预备的\r\n是眼睛未曾看见，\r\n耳朵未曾听见，\r\n人心也未曾想到的。\r\n只有　神藉着圣灵向我们显明了，因为圣灵参透万事，就是　神深奥的事也参透了。除了在人里头的灵，谁知道人的事？像这样，除了　神的灵，也没有人知道　神的事。我们所领受的，并不是世上的灵，乃是从　神来的灵，叫我们能知道　神开恩赐给我们的事。并且我们讲说这些事，不是用人智慧所指教的言语，乃是用圣灵所指教的言语，将属灵的话解释属灵的事（或译：将属灵的事讲与属灵的人）。', 1);
INSERT INTO `test` VALUES (33, '哥林多前书 2:14 和合本', '然而，属血气的人不领会　神圣灵的事，反倒以为愚拙，并且不能知道，因为这些事惟有属灵的人才能看透。', 1);
INSERT INTO `test` VALUES (34, '以弗所书 6:10-17 和合本', '我还有末了的话：你们要靠着主，倚赖他的大能大力作刚强的人。要穿戴　神所赐的全副军装，就能抵挡魔鬼的诡计。因我们并不是与属血气的争战（原文是摔跤；下同），乃是与那些执政的、掌权的、管辖这幽暗世界的，以及天空属灵气的恶魔争战。所以，要拿起　神所赐的全副军装，好在磨难的日子抵挡仇敌，并且成就了一切，还能站立得住。所以要站稳了，用真理当作带子束腰，用公义当作护心镜遮胸，又用平安的福音当作预备走路的鞋穿在脚上。此外，又拿着信德当作盾牌，可以灭尽那恶者一切的火箭；并戴上救恩的头盔，拿着圣灵的宝剑，就是　神的道；', 1);
INSERT INTO `test` VALUES (35, '雅各书 4:7 和合本', '故此，你们要顺服　神。务要抵挡魔鬼，魔鬼就必离开你们逃跑了。', 1);
INSERT INTO `test` VALUES (36, '哥林多后书 10:4-5 和合本', '我们争战的兵器本不是属血气的，乃是在　神面前有能力，可以攻破坚固的营垒，将各样的计谋，各样拦阻人认识　神的那些自高之事，一概攻破了，又将人所有的心意夺回，使他都顺服基督。', 1);
INSERT INTO `test` VALUES (37, '以赛亚书 46:10 和合本', '我从起初指明末后的事，\r\n从古时言明未成的事，\r\n说：我的筹算必立定；\r\n凡我所喜悦的，我必成就。', 1);
INSERT INTO `test` VALUES (38, '玛拉基书 2:15 和合本', '虽然　神有灵的余力能造多人，他不是单造一人吗？为何只造一人呢？乃是他愿人得虔诚的后裔。所以当谨守你们的心，谁也不可以诡诈待幼年所娶的妻。', 1);
INSERT INTO `test` VALUES (39, '彼得前书 1:23 和合本', '你们蒙了重生，不是由于能坏的种子，乃是由于不能坏的种子，是藉着　神活泼常存的道。', 0);

SET FOREIGN_KEY_CHECKS = 1;
